        SELECT friend_id FROM "public"."friends" WHERE user_id = target_user_id
        UNION
        SELECT user_id FROM "public"."friends" WHERE friend_id = target_user_id
    )
    AND get_mutual_friends_count(target_user_id, potential_friend.id) > 0;
END;
$$;


